select o.id,o.name as Table_Name,i.name as Index_Name, i.index_id,i.type as index_type,i.type_desc,i.is_unique,i.ignore_dup_key,i.is_primary_key,i.is_unique_constraint, i.fill_factor,i.is_padded,i.is_disabled,i.is_hypothetical,i.allow_row_locks,i.allow_page_locks,i.data_space_id,ds.Name as data_space_name  from sys.indexes i 	INNER JOIN dbo.sysobjects o on (o.xtype='U' and o.id=i.object_id and i.is_primary_key=1 AND upper(o.Name)='COM_DATABASE_LOG') 	LEFT JOIN sys.data_spaces ds on i.data_space_id =ds.data_space_id 